DROP TABLE IF EXISTS `june_11_2013_users`#endQuery
CREATE TABLE `june_11_2013_users` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `user_login` varchar(60) NOT NULL DEFAULT '',
  `user_pass` varchar(64) NOT NULL DEFAULT '',
  `user_nicename` varchar(50) NOT NULL DEFAULT '',
  `user_email` varchar(100) NOT NULL DEFAULT '',
  `user_url` varchar(100) NOT NULL DEFAULT '',
  `user_registered` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `user_activation_key` varchar(60) NOT NULL DEFAULT '',
  `user_status` int(11) NOT NULL DEFAULT '0',
  `display_name` varchar(250) NOT NULL DEFAULT '',
  PRIMARY KEY (`ID`),
  KEY `user_login_key` (`user_login`),
  KEY `user_nicename` (`user_nicename`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8#endQuery
INSERT INTO `june_11_2013_users` (`ID`, `user_login`, `user_pass`, `user_nicename`, `user_email`, `user_url`, `user_registered`, `user_activation_key`, `user_status`, `display_name`) VALUES ('1', 'admin', '$P$Bwyy9zIYruN0h6pWHNHCBiAZn7nXgb0', 'admin', 'adrianjonmiller@gmail.com', '', '2013-06-11 19:22:48', '', '0', 'admin'),('2', 'larryhayden', '$P$BSCYYKr5NYjdNLCI9eQSP2Epj70g1I/', 'larryhayden', 'larry@orovillehopecenter.org', '', '2013-06-20 20:14:25', '', '0', 'Larry Layden');#endQuery
DROP TABLE IF EXISTS `old_commentmeta`#endQuery
CREATE TABLE `old_commentmeta` (
  `meta_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `comment_id` bigint(20) unsigned NOT NULL DEFAULT '0',
  `meta_key` varchar(255) DEFAULT NULL,
  `meta_value` longtext,
  PRIMARY KEY (`meta_id`),
